mapper.select:
select , ,
from products product left join categories category on category.id = product.category_id left join skus sku on sku.product_id = product.id where 1=1 and (product.is_deleted = 0 or product.is_deleted is null)
and product.category_id = #{categoryId,jdbcType=INTEGER}

select , ,
from products product left join categories category on category.id = product.category_id left join skus sku on sku.product_id = product.id where 1=1 and (product.is_deleted = 0 or product.is_deleted is null)
and product.category_id = #{categoryId,jdbcType=INTEGER}
and category.brand_id = #{brandId,jdbcType=INTEGER}

mapper.update:
update products
set is_deleted = #{deleted,jdbcType=BOOLEAN},
deleted_at = #{deletedAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
