mapper.select:
select
,

from stores store
left join chains chain on store.chain_id = chain.id
where store.name = #{name,jdbcType=VARCHAR}
      and store.chain_id = #{chainId,jdbcType=INTEGER}

select ,
from stores store left join chains chain on store.chain_id = chain.id left join store_tiers store_tier on store_tier.store_id = store.id where store.chain_id = #{chainId,jdbcType=INTEGER}
and store.name like concat('%', #{search, jdbcType=VARCHAR}, '%')
